/**
 * Official Type definitions for LemonadeJS plugins
 * https://lemonadejs.net
 * Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
 */

declare function Color(el: HTMLElement, options?: Color.Options): Color.Instance;

declare namespace Color {
    interface Options {
        /** Available colors */
        palette?: string[];
        /** Initial state */
        closed?: boolean;
        /** Picker Type */
        type?: 'inline' | 'input' | undefined;
        /** Current value */
        value?: string;
        /** On open event */
        onopen?: () => void;
        /** On close event */
        onclose?: () => void;
        /** On update event */
        onchange?: (self: object, value: string) => void;
    }

    interface Instance {
        /** Current palette */
        palette: string[];
        /** Picker state */
        closed: boolean;
        /** Current value */
        value: string;
    }
}

export default Color;